<?
  function resize_image($_path = null, $_w = null, $_h = null, $_mode = null, $_oversize = null) {
    // INIT
    defined('MAIN_DIR') or define('MAIN_DIR', dirname(__FILE__));

    // string
    $path = isset($_path) ? (string) $_path : null;
    $mode = isset($_mode) ? (string) $_mode : 'contain';

    // int
    $w = null;
    $h = null;
    if (isset($_w) && is_numeric($_w) && !empty($_w))
      $w = (int) $_w;
    if (isset($_h) && is_numeric($_h) && !empty($_h))
      $h = (int) $_h;


    // bool
    $oversize = isset($_oversize) ? (int) (boolean) $_oversize : 0;

    // defaults
    $noimage_path = MAIN_DIR . '/no-image.jpg';
    // PROCESS
    // if there's no default image - create it
    if (!is_file($noimage_path)) {
      $tmp1 = "data:image/jpeg;base64,";
      $tmp2 = explode(',', $tmp1);
      $fh = fopen($noimage_path, 'wb');
      fwrite($fh, base64_decode($tmp2[1]));
      fclose($fh);
    }

    // check mode is allowed
    in_array($mode, array(
      'contain',
      'oneside',
      'cover',
      'fill',
      'hard',
    )) || exit('[resize_image] Unknown $mode - ' . $mode);

    // return if user haven't brain
    if (($w < 0) && ($h < 0))
      exit("[resize_image] Illegal size \$w: {$w} or \$h: {$h}");

    // if image's not exists - return default image
    $path = realpath($path);
    (!empty($path) && is_file($path)) or $path = $noimage_path;

    // get file info
    $matrix = array(
      'dirname',
      'basename',
      'filename',
      'extension'
    );
    $tmp = pathinfo($path);
    $arr = array_merge($matrix, $tmp);
    $old_dirname = $arr['dirname'];
    $old_basename = $arr['basename'];
    $old_filename = $arr['filename'];
    $old_extension = $arr['extension'];
    $old_mime = image_type_to_mime_type(exif_imagetype($path));

    // check is there an image
    $function_imagecreate = '';
    $function_imagecopy = '';
    $function_imagesave = '';
    switch($old_mime) {
      case 'image/jpg':
      case 'image/jpeg':
        $function_imagecreate = 'imagecreatetruecolor';
        $function_imagecopy = 'imagecreatefromjpeg';
        $function_imagesave = 'imagejpeg';
        break;
      case 'image/gif':
        $function_imagecreate = 'imagecreate';
        $function_imagecopy = 'imagecreatefromgif';
        $function_imagesave = 'imagegif';
        break;
      case 'image/png':
        $function_imagecreate = 'imagecreate';
        $function_imagecopy = 'imagecreatefrompng';
        $function_imagesave = 'imagepng';
        break;
      case 'image/bmp':
        $function_imagecreate = 'imagecreate';
        $function_imagecopy = 'imagecreatefrombmp';
        $function_imagesave = 'imagebmp';
        break;
      default:
        exit('[resize_image] Unknown mime-type - ' . $old_mime);
    }

    !is_dir($var = MAIN_DIR . "/temp") && mkdir($var, 0755);
    !is_dir($var = MAIN_DIR . "/temp/img") && mkdir($var, 0755);

    // get old image data
    $old_width = 0;
    $old_height = 0;
    list($old_width, $old_height) = getimagesize($path);

    // check oversize
    if (!$oversize)
      if (
        (($w !== null) && ($w > $old_width)) ||
        (($h !== null) && ($h > $old_height))
      )
        return realpath($path);

    // init buffer
    $dst_x = 0;
    $dst_y = 0;
    $dst_height = 0;
    $dst_width = 0;
    $src_x = 0;
    $src_y = 0;
    $src_width = 0;
    $src_height = 0;

    $src_width = $old_width;
    $src_height = $old_height;

    // process new image size
    if (in_array($mode, array('contain'))) {
      if (
        ($w === null) &&
        ($h === null)
      ) {
        exit('[resize_image] Contain mode need at least one side to be defined.');
      }

      if (($w !== null) && ($h === null)) {
        $dst_width = $w;
        $dst_height = ceil($old_height * ($w / $old_width));
      }

      if (($h !== null) && ($w === null)) {
        $dst_height = $h;
        $dst_width = ceil($old_width * ($h / $old_height));
      }

      if (($w !== null) && ($h !== null)) {
        if ($old_width >= $old_height) {
          $dst_width = $w;
          $dst_height = ceil($old_height * ($w / $old_width));
        } else {
          $dst_height = $h;
          $dst_width = ceil($old_width * ($h / $old_height));
        }
      }

      $dst = $function_imagecreate($dst_width, $dst_height);
      $bg_color = imagecolorallocate($dst, 255, 255, 255);
      imagefilledrectangle($dst, 0, 0, $dst_width, $dst_height, $bg_color);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $dst,
        $src,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $src_width,
        $src_height
      );
    }

    elseif (in_array($mode, array('oneside'))) {
      if (($w === null) && ($h === null))
        exit('[resize_image] One-side mode requires at least one side to be defined.');

      if (($w !== null) && ($h !== null))
        exit('[resize_image] One-side mode requires only one side to be defined.');

      if (($w !== null) && ($h === null)) {
        $dst_width = $w;
        $dst_height = ceil($old_height * ($w / $old_width));
      }

      if (($h !== null) && ($w === null)) {
        $dst_height = $h;
        $dst_width = ceil($old_width * ($h / $old_height));
      }

      $dst = $function_imagecreate($dst_width, $dst_height);
      $bg_color = imagecolorallocate($dst, 255, 255, 255);
      imagefilledrectangle($dst, 0, 0, $dst_width, $dst_height, $bg_color);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $dst,
        $src,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $src_width,
        $src_height
      );
    }

    elseif (in_array($mode, array('cover'))) {
      if (($w === null) && ($h === null))
        exit('[resize_image] Cover mode requires at least one side to be defined.');

      if (($w !== null) && ($h === null)) {
        $h = $old_height;
        $dst_width = $w;
        $dst_height = ceil($old_height * ($w / $old_width));
      }

      if (($h !== null) && ($w === null)) {
        $w = $old_width;
        $dst_height = $h;
        $dst_width = ceil($old_width * ($h / $old_height));
      }

      if (($w !== null) && ($h !== null)) {
        if ($w >= $h) {
          $dst_width = $w;
          $dst_height = ceil($old_height * ($w / $old_width));
        } else {
          $dst_height = $h;
          $dst_width = ceil($old_width * ($h / $old_height));
        }
      }

      $predst = $function_imagecreate($dst_width, $dst_height);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $predst,
        $src,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $src_width,
        $src_height
      );
      $dst_x = ceil(($w / 2) - ($dst_width / 2));
      $dst_y = ceil(($h / 2) - ($dst_height / 2));
      $dst = $function_imagecreate($w, $h);
      $bg_color = imagecolorallocate($dst, 255, 255, 255);
      imagefilledrectangle($dst, 0, 0, $w, $h, $bg_color);
      @imagecopyresampled(
        $dst,
        $predst,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $dst_width,
        $dst_height
      );
      imagedestroy($predst);
    }

    elseif (in_array($mode, array('fill'))) {
      $src_width = $old_width;
      $src_height = $old_height;

      if (($w === null) && ($h === null))
        exit('[resize_image] Cover mode requires at least one side to be defined.');

      if (($w !== null) && ($h === null)) {
        $h = $old_height;
        $dst_width = $w;
        $dst_height = ceil($old_height * ($w / $old_width));
      }

      if (($h !== null) && ($w === null)) {
        $w = $old_width;
        $dst_height = $h;
        $dst_width = ceil($old_width * ($h / $old_height));
      }

      if ($w <= $h) {
        $dst_width = $w;
        $dst_height = ceil($old_height * ($w / $old_width));
      } else {
        $dst_height = $h;
        $dst_width = ceil($old_width * ($h / $old_height));
      }

      $predst = $function_imagecreate($dst_width, $dst_height);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $predst,
        $src,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $src_width,
        $src_height
      );
      $dst_x = ceil(($w / 2) - ($dst_width / 2));
      $dst_y = ceil(($h / 2) - ($dst_height / 2));
      $dst = $function_imagecreate($w, $h);
      $bg_color = imagecolorallocate($dst, 255, 255, 255);
      imagefilledrectangle($dst, 0, 0, $w, $h, $bg_color);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $dst,
        $predst,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $dst_width,
        $dst_height,
        $dst_width,
        $dst_height
      );
      imagedestroy($predst);
    }

    elseif (in_array($mode, array('hard'))) {
      // if not oversize - use old image sizes
      $src_width = $old_width;
      $src_height = $old_height;

      if ($w === null) $w = $old_width;
      if ($h === null) $h = $old_height;

      $dst = $function_imagecreate($w, $h);
      $bg_color = imagecolorallocate($dst, 255, 255, 255);
      imagefilledrectangle($dst, 0, 0, $w, $h, $bg_color);
      $src = $function_imagecopy($path);
      @imagecopyresampled(
        $dst,
        $src,
        $dst_x,
        $dst_y,
        $src_x,
        $src_y,
        $w,
        $h,
        $src_width,
        $src_height
      );
    }
    else
      exit('[resize_image] Unknown $mode - ' . $mode);

    $file_w = imagesx($dst);
    $file_h = imagesy($dst);

    !is_dir($var = MAIN_DIR . "/temp/img/{$file_w}-{$file_h}") && mkdir($var, 0755);
    $hash = md5($path . filesize($path) . filemtime($path) . $old_mime . $w . $h);
    $new_path = $var . '/image_' . $hash . '.' . $old_extension;

    // return path if exists
    if (is_file($new_path))
      return realpath($new_path);

    // save image
    $function_imagesave($dst, $new_path, 100);
    imagedestroy($src);
    imagedestroy($dst);

    // remove exif data from JPEG
    if (class_exists('Imagick') && in_array($old_mime, array('image/jpg', 'image/jpeg'))) {
      $imagick = new Imagick($new_path);
      $imagick->setCompression(Imagick::COMPRESSION_JPEG);
      $imagick->setCompressionQuality(100);
      $imagick->stripImage();
      $imagick->writeImage($new_path);
    }

    return realpath($new_path);
  }
